import pandas as pd
import math
import numpy as np  

def get_word_difficulty_index(word, df):
        try:
                freq = df.loc[df['word'] == word.lower()]['count'].values[0]
                return 1000 / math.log10(freq)**3 / 14.465082*10
        except:
                return np.nan



